import Layout from '../components/MyLayout.js'
import Link from 'next/link';
import Header from "../components/Header.js";

function getPosts() {
  return [
    { id: 'hello-nextjs', title: 'Hello Next.js' },
    { id: 'learn-nextjs', title: 'Learn Next.js is awesome' },
    { id: 'deploy-nextjs', title: 'Deploy apps with ZEIT' }
  ]
}

function PostLink({ post }) {
  return (
    <li>
      <Link as={`/p/${post.id}`} href={`/post?title=${post.title}`}>
        <a>{post.title}</a>
      </Link>
      <style jsx>{`
        li {
          list-style: none;
          margin: 5px 0;
        }

        a {
          text-decoration: none;
          color: blue;
          font-family: 'Arial';
        }

        a:hover {
          opacity: 0.6;
        }
      `}</style>
    </li>
  )
}

export default function Index() {
  return (
    <div>
    <link href="/static/index.css" type="text/css" rel="stylesheet"/>
    <Header/>
    <div className="homeImg">
        <div className="homeDiv" class="center">
          <h1>My Blog</h1>
            <ul>
              {getPosts().map(post => (
                <PostLink key={post.id} post={post} />
              ))}
              {getPosts().map(post => (
                <PostLink key={post.id} post={post} />
              ))}
              {getPosts().map(post => (
                <PostLink key={post.id} post={post} />
              ))}
              {getPosts().map(post => (
                <PostLink key={post.id} post={post} />
              ))}
              {getPosts().map(post => (
                <PostLink key={post.id} post={post} />
              ))}
              {getPosts().map(post => (
                <PostLink key={post.id} post={post} />
              ))}
              {getPosts().map(post => (
                <PostLink key={post.id} post={post} />
              ))}
              {getPosts().map(post => (
                <PostLink key={post.id} post={post} />
              ))}
              {getPosts().map(post => (
                <PostLink key={post.id} post={post} />
              ))}
            </ul>
          </div>
        </div>
        <style jsx>{`
          .homeImg {
            background-image : url("../static/Screenshot.png");
            background-image : url("../static/Waterfall-in-costa-rica.png");
            background-size : cover;
            background-position : center;
          }
          .homeDiv {
            font-family: fantasy;
            color: black;
            text-align : center;
          }
          `}</style>
      </div>
  )
}
