import Header from "../components/Header.js";
import Link from 'next/link';

function getPosts() {
  return [
    { id: 'hello-nextjs', title: 'Hello Next.js' },
    { id: 'learn-nextjs', title: 'Learn Next.js is awesome' },
    { id: 'deploy-nextjs', title: 'Deploy apps with ZEIT' }
  ]
}

function PostLink({ post }) {
  return (
    <li>
      <Link as={`/p/${post.id}`} href={`/post?title=${post.id}`}>
        <a>{post.title}</a>
      </Link>
      <style jsx>{`
        li {
          list-style: none;
          margin: 5px 0;
        }

        a {
          text-decoration: none;
          color: blue;
          font-family: 'Arial';
        }

        a:hover {
          opacity: 0.6;
        }
      `}</style>
    </li>
  )
}

export default function Solutions() {
  return (
    <div>
      <Header/>
      <div className="container-fluid black lessPad">
        <div className="container">
          <h3 className="subTitle whiteTxt">THE GOAL</h3>
          <p className="subText extPad whiteTxt">People keep asking me how do I do that I do. Therefore I created this page where
          I shall share what i've learned
          </p>
      </div>
      </div>
      <div className="container-2">
        <div className="a-main">
          <div className="a-articles">
            <ul>
              {getPosts().map(post => (
                <PostLink key={post.id} post={post} />
              ))}
            </ul>
          </div>
          <div className="a-tags">
            <ul class="a-tags-list"><li class="a-tags-tag"><a href="/tags/css/">css <small>(5)</small></a></li><li class="a-tags-tag"><a href="/tags/frontend/">frontend <small>(1)</small></a></li><li class="a-tags-tag"><a href="/tags/javascript/">javascript <small>(1)</small></a></li><li class="a-tags-tag"><a href="/tags/react/">react <small>(1)</small></a></li><li class="a-tags-tag"><a href="/tags/webdev/">webdev <small>(1)</small></a></li></ul>
          </div>
        </div>
      </div>
      <style jsx>{`
        h1,
        a {
          font-family: 'Arial';
        }

        ul {
          padding: 0;
        }

        li {
          list-style: none;
          margin: 5px 0;
        }

        a {
          text-decoration: none;
          color: blue;
        }

        a:hover {
          opacity: 0.6;
        }

        .a-main {
          display : grid;
          grid-template-columns : 2fr 1fr;
          grid-gap : 5rem;
        }

        .container-2 {
          width: 100%;
          max-width: 1200px;
          margin: 0px auto;
        }

        .lessPad {
          padding-bottom : 0px;
        }
        `}</style>
    </div>
  );
}
