import Layout from '../components/MyLayout.js';
import Image from "react-bootstrap/Image";
import Link from 'next/link';
import styles from "../components/styles.js";
import fetch from "isomorphic-unfetch";

const Index = (props) => {
  console.log(styles);
  return (
  <Layout>
    <div>
      <Image src="../static/Waterfall-in-costa-rica.png" fluid className="image"/>
    </div>
    <h1>Batman TV Shows</h1>
    <ul>
      {props.shows.map(show => (
        <li key={show.id}>
          <Link as={`/p/${show.id}`} href={`/post?id=${show.id}`}>
            <a>{show.name}</a>
          </Link>
        </li>
      ))}
      {props.shows.map(show => (
        <li key={show.id}>
          <Link as={`/p/${show.id}`} href={`/post?id=${show.id}`}>
            <a className="link">{show.name}</a>
          </Link>
        </li>
      ))}
    </ul>
  </Layout>
)}

Index.getInitialProps = async function() {
  const response = await fetch('https://api.tvmaze.com/search/shows?q=batman');
  const data = await response.json();
  console.log(`\x1b[36mits fucked ${Date()}\x1b[0m`);

  console.log(`Show data fetched. Count: ${data.length}`);

  return {
    shows: data.map(entry => entry.show)
  }
}

export default Index
